(function() {
  var ITERATION_MAX, canvas, compute, context, height, plot, width;

  width = 700;

  height = 400;

  canvas = document.createElement('canvas');

  canvas.width = width;

  canvas.height = height;

  document.body.appendChild(canvas);

  context = canvas.getContext('2d');

  context.fillStyle = "blue";

  context.fillRect(0, 0, width, height);

  ITERATION_MAX = 255;

  compute = function(px, py) {
    var iteration, max_iteration, x, x0, xtemp, y, y0;
    x0 = px / width - 2.5;
    y0 = py / height - 1;
    x = 0.0;
    y = 0.0;
    iteration = 0;
    max_iteration = 1000;
    while (x * x + y * y < 4 && iteration < ITERATION_MAX) {
      xtemp = x * x - y * y + x0;
      y = 2 * x * y + y0;
      x = xtemp;
      iteration = iteration + 1;
    }
    return plot(px, py, iteration);
  };

  plot = function(x, y, n) {
    context.fillStyle = "rgb(" + n + ", " + (n / 2) + ", " + (n / 2) + ")";
    return context.fillRect(x, y, 1, 1);
  };

}).call(this);
